mapper.select:
 		select accesstoken
		  from t_wx_config
		 where appid = #{appid}

mapper.update:
 		update t_wx_config a
		  set a.accesstoken = #{accesstoken}, rec_date = sysdate
		where appid = #{appid}

 		update t_wx_config a
		  set a.accesstoken = #{JsApiTicket}, jsapi_rec_date = sysdate
		where appid = #{appid}
