mapper.select:
 		select appid
		  from t_wx_config
		 where appid = #{appid}

mapper.update:
 		update t_wx_config a
		  set a.accesstoken = #{accesstoken}, rec_date = sysdate
		where appid = #{appid}

 		update t_wx_config a
		  set a.jsapiticket = #{jsApiTicket}, jsapi_rec_date = sysdate
		where appid = #{appid}
